import React, {Component} from "react";

class App extends Component {
    constructor() {
        super();
        this.state = {
            number: 0
        };
    }
    eventHandler() {
        this.setState({
            number:1
        })
    }

    
    render(){
        return (
            <div className="container">
                <span>{this.state.number}</span>
                <button onClick={this.eventHandler} className="btn btn-primary">
                    click me
                </button>
            </div>
        );
     }
}

export default App
