class App extends Component {
    constructor() {
 
        super();
        this.state = {
            number: 0
        }
    }
    eventHndler = () => {console.log('test')}
        

    
    render() { 
        return (
            <div className="container">
              <span>{this.state.number}</span>
              <button onClick={this.eventHandler} className="btn btn-primary">
                click me
              </button>
            </div>
          );
    }
}
export default App
